var marketcloud = require('../index.js'),
	mocha = require('mocha'),
	chai = require('chai');

var expect = chai.expect;

var client = null;
var category_id = null;
describe('Categories',function(){

	before(function(){

		client = new marketcloud.Client({
			public_key : process.env.MARKETCLOUD_PUBLIC_KEY,
    		secret_key : process.env.MARKETCLOUD_SECRET_KEY
		})
	})


	it('Should retrieve a list of categories',function(){
		return client.categories.list({})
			.then(function(response){
				
				expect(response.length).to.not.equal(0);
				expect(response.length).to.be.at.most(20);
				category_id = response[0].id;

			})
	});


	it('Should retrieve a specific category',function(){
		return client.categories.getById(category_id)
			.then(function(response){
				
				expect(response.id).to.equal(category_id);
				
			})
	})

	it('Should create a category',function(){
		var category = {
			name : 'American Comic books',
			image_url : 'http://cinemasalem.com/wordpress/wp-content/uploads/2012/03/marvel-and-dc-logo.jpg'
		}
		return client.categories.create(category)
					.then(function(response){
						var category_id = response.id
						
					})

	})

	it('Should update a category',function(){
		var update = {
			name : 'Comics'
		}
		return client.categories.update(category_id,update)
								.then(function(response){
									
								})
	})


	it('Should delete a category',function(){
		return client.categories.delete(category_id)
								.then(function(response){
									
								})
	})

	
})